changeSet.sql:
CREATE TABLE user(
userUUID varchar(64) NOT NULL,
name varchar(64),
walletBalance int,
PRIMARY KEY(userUUID)
);

CREATE TABLE stockdetail(
symbol varchar(64) NOT NULL,
count int,
price int,
PRIMARY KEY(symbol)
);

CREATE TABLE userstockdetail(
userUUID varchar(64) NOT NULL,
symbol varchar(64) NOT NULL,
count int,
PRIMARY KEY(userUUID, symbol)
);
